import React, { Component } from 'react'
import {Link,Redirect} from 'react-router-dom'
import axios from 'axios'
import Card from '../Card/Card'
import './Users.css'

export default class Users extends Component {
    constructor(props){
        super(props)
        // const token = localStorage.getItem("token")
        let loggedIn = true

        // if(token == null){
        //     loggedIn =false
        // }
        this.state = {
            users: [],
            loggedIn
        }


    }
    componentDidMount () {
        const token = localStorage.getItem("token")
        const config = {
            headers: { Authorization: `Bearer ${token}` }
        };
        axios.get('https://apertum-interview.herokuapp.com/api/users', config)
            .then((response) => {
                const user =response.data
                this.setState({users: [user]})
                // const users = ;
                // console.log(users)
                // const updatedUsers = users.map(user =>{
                //     return{
                //         ...user
                //     }
                // })
                // ;
                console.log( response );
            } )
            .catch(error => {
                console.log(error);
                // this.setState({error: true});
            });
    }

    // componentWillUnmount () {
    //     this.setState({this.loggedIn});
    //   }


    render() {
        // if(this.state.loggedIn === false){
        //     return <Redirect to="/" />
        // }

        // let users = <p style={{textAlign: 'center'}}>Something went wrong!</p>
        // if(!this.state.error){
        //     users =this.state.users.map(user =>{
        //         return <Users
        //             key={user.id}
        //             title={user.accountId}
        //             fName = {user.firstName}
        //             lName = {user.lastName}
        //             />
        //     })
        const users = this.state.users.map(user =>{
            return <Card key={user.accountId} title={user.firstName} />
            })

        return (
            <div >
                <h1>Welcome to all Users</h1>
                <section className="Users" >
                    {users}
                </section>
                <Link to="/">Login Again</Link>
            </div>
        )
    }
}
