import React,{Component} from 'react';
import './Login.css'
import axios from 'axios'

class Login extends Component{
    constructor(props){
        super(props)
        let isLoggedIn = false

        this.state = {
            Username:'',
            password:'',
            isLoggedIn
        }
    }

        onSubmit(e){
            e.preventDefault()

            const userLogin = {
                "accountId": "admin",
                "pswd": "123456"
            }

            axios.post('https://apertum-interview.herokuapp.com/api/user/login',userLogin)
                .then((response) => {
                    console.log(response);
                  }, 
                  (error) => {
                    console.log(error);
                  })

        }


    render(){
        return(
            <div className="box-container">
                <form className="box" >
                    <h2 className="">Hi User</h2>
                    <input type="text" placeholder="Username"/>
                    <input type="password" placeholder="Password"/>
                    <button type="submit" onSubmit={this.onSubmit}>Login</button>
                </form>

            </div>
        )
    }
}

export default Login
