import React,{Component} from 'react';
import './Login.css'
import axios from 'axios'

class Login extends Component{
    constructor(props){
        super(props)
        let isLoggedIn = false

        this.onChange = this.onChange.bind(this)
        this.onSubmit = this.onSubmit.bind(this)


        this.state = {
            Username:'',
            password:'',
            isLoggedIn
        }

    }

    onChange(e) {
        this.setState({
            [e.target.name] : e.target.value
        })
    }

    onSubmit(e){
        e.preventDefault()

        const userLogin = {
            "accountId": "admin",
            "pswd": "123456"
        }

        axios.post('https://apertum-interview.herokuapp.com/api/user/login',userLogin)
            .then((response) => {
                console.log(response);
                }, 
                (error) => {
                console.log(error);
                })

        }


    render(){
        return(
            <div className="box-container">
                <form className="box" onSubmit={this.onSubmit} >
                    <h2 className="">Hi User</h2>
                    <input type="text" placeholder="Username" value={this.state.Username} onChange={this.onChange}/>
                    <input type="password" placeholder="Password" value={this.state.password} onChange={this.onChange}/>
                    <button type="submit" >Login</button>
                </form>

            </div>
        )
    }
}

export default Login
